import React, { Component } from 'react';
import './App.css';
// import SearchButton from './components/SearchButton';
import Random from './components/Random';
import SearchField from './components/SearchField';
import Scroll from './components/Scroll';
// import SearchContainer from './components/SearchContainer';

/* function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>Get a Life</h1>
        <Random /> 
      </header>
      <SearchContainer />
    </div>
  );
} */


class App extends Component {
  constructor() {
      super()
      // this.state on se joka muuttuu ohjelmassa dynaamisesti, 
      // tässä robotti-array (joka aluksi tyhjä) ja hakukenttä
      this.state = {
          funs: [],
          searchvalue: ''
      }
  }

  componentDidMount() {
      // fetch tekee http-requestin
      fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => {
          return response.json();
      })
      .then(users => {
          this.setState({ funs: users })
      });

  }

  // nimi on keksitty, ei varattu
  onSearchChange = (event) => {
      this.setState({ searchvalue: event.target.value })
  }

  render() {
      const filteredFuns = this.state.funs.filter(fun =>{
          return fun.name.toLowerCase().includes(this.state.searchvalue.toLowerCase());
      })
      // if else sitä varten että kestää kauan ladata, ei muuta syytä! Voi siis jättää pois
      if (this.state.funs.length === 0) {
          return <h1>Loading...</h1>
      }
      else {
          return (
              <div className='container'>
                  <h1>Get a Life</h1>
                  <Random /> 
                  <SearchField searchChange={this.onSearchChange}/>
                  <Scroll>
                      <CardList funs={filteredFuns}/>
                  </Scroll>
              </div>
          );
      }

  }

}

export default App;
